import React, { useRef, useState } from 'react';
import Webcam from 'react-webcam';

function CameraAccess() {
    const webcamRef = useRef(null);
    const [capturedImage, setCapturedImage] = useState(null);

    const captureImage = () => {
        const imageSrc = webcamRef.current.getScreenshot();
        setCapturedImage(imageSrc);
    };

    return (
        <div style={{ margin: "20px 0" }}>
            <h2>Akses Kamera</h2>
            <Webcam 
                ref={webcamRef} 
                screenshotFormat="image/jpeg" 
                style={{ width: "100%", marginBottom: "10px", border: "1px solid #ddd", borderRadius: "8px" }} 
            />
            <button 
                onClick={captureImage} 
                style={{ padding: "10px 15px", backgroundColor: "#2196F3", color: "white", border: "none", cursor: "pointer" }}
            >
                Tangkap Gambar
            </button>
            {capturedImage && (
                <div style={{ marginTop: "20px" }}>
                    <h3>Gambar Tertangkap</h3>
                    <img 
                        src={capturedImage} 
                        alt="Captured" 
                        style={{ width: "100%", border: "1px solid #ddd", borderRadius: "8px" }} 
                    />
                </div>
            )}
        </div>
    );
}

export default CameraAccess;
