import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../App.css'; // Import CSS untuk Navbar

function NavBar() {
  const [isNavOpen, setIsNavOpen] = useState(false); 
  const location = useLocation(); 
  const [prevScrollPos, setPrevScrollPos] = useState(0); 
  const [visible, setVisible] = useState(true); 
  
  // Efek untuk menutup navbar saat berpindah halaman
  useEffect(() => {
    setIsNavOpen(false);
  }, [location]);

  // Fungsi untuk menangani scroll
  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset; 
    setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 10);
    setPrevScrollPos(currentScrollPos);
  };

  // Hook untuk mendeteksi scroll event
  useEffect(() => {
    window.addEventListener('scroll', handleScroll); 
    return () => {
      window.removeEventListener('scroll', handleScroll); 
    };
  }, [prevScrollPos]);

  return (
    <Navbar
      className={`navbar-fixed-top ${visible ? 'navbar-visible' : 'navbar-hidden'}`} 
      expand="lg"
      expanded={isNavOpen} 
    >
      <Container>
        <Navbar.Brand as={Link} to="/">
          <img src="/images/logo.png" alt="Logo" className="d-inline-block align-top"/>
        </Navbar.Brand>
        <Navbar.Toggle 
          aria-controls="navbar-nav" 
          onClick={() => setIsNavOpen(!isNavOpen)} 
        />
        <Navbar.Collapse id="navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link as={Link} to="/" onClick={() => setIsNavOpen(false)}>Home</Nav.Link>
            <Nav.Link as={Link} to="/TentangKami" onClick={() => setIsNavOpen(false)}>Tentang Kami</Nav.Link>
            <Nav.Link as={Link} to="/Diagnosis" onClick={() => setIsNavOpen(false)}>Diagnosis</Nav.Link>
            <Nav.Link as={Link} to="/History" onClick={() => setIsNavOpen(false)}>Riwayat Pengguna</Nav.Link>
            <Nav.Link as={Link} to="/Auth" onClick={() => setIsNavOpen(false)}>Login</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
