// Register.jsx
import React, { useState } from 'react';
import { Button, Form, Alert, Container, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    if (!name || !email || !password) {
      setError('Semua field harus diisi');
      return;
    }
    // Simulasi registrasi berhasil
    setError('');
    navigate('/login'); // Ganti dengan halaman login setelah registrasi
  };

  return (
    <Container fluid className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh', backgroundColor: '#f1f1f1' }}>
      <Row className="w-100">
        <Col xs={12} md={6} className="d-flex justify-content-center align-items-center">
          <div className="w-75 bg-white p-4 shadow rounded">
            <h3 className="mb-4 text-center">Register</h3>
            {error && <Alert variant="danger">{error}</Alert>}

            <Form onSubmit={handleRegisterSubmit}>
              <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Label>Full Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter full name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>

              <Button variant="primary" type="submit" className="w-100">
                Register
              </Button>
            </Form>

            <div className="mt-3 text-center">
              <small>
                Already have an account?{' '}
                <Button variant="link" onClick={() => navigate('/login')}>
                  Login here
                </Button>
              </small>
            </div>
          </div>
        </Col>

        {/* Kolom Gambar */}
        <Col xs={12} md={6} className="d-none d-md-flex justify-content-center align-items-center">
          <img
            src="https://via.placeholder.com/500x500"
            alt="Register Illustration"
            style={{ maxWidth: '100%', height: 'auto' }}
          />
        </Col>
      </Row>
    </Container>
  );
}

export default Register;
