import React, { useRef } from "react";
import Webcam from "react-webcam";
import axios from "axios";

function CameraUploader({ onDiagnose }) {
  const webcamRef = useRef(null);

  const capturePhoto = async () => {
    const imageSrc = webcamRef.current.getScreenshot();
    // Send imageSrc to backend
    const response = await axios.post("/api/diagnose", { image: imageSrc });
    onDiagnose(response.data);
  };

  return (
    <div>
      <Webcam ref={webcamRef} screenshotFormat="image/jpeg" />
      <button onClick={capturePhoto}>Capture and Diagnose</button>
    </div>
  );
}

export default CameraUploader;
