import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Upload() {
  const [file, setFile] = useState(null);
  const [features, setFeatures] = useState(null);
  const navigate = useNavigate();

  const handleFileUpload = async (event) => {
    const uploadedFile = event.target.files[0];
    setFile(uploadedFile);
    // Extract features using backend or locally
    const extractedFeatures = [0.1, 0.2, 0.3, 0.4]; // Replace with actual feature extraction logic
    setFeatures(extractedFeatures);
  };

  const handlePrediction = async () => {
    try {
      const response = await axios.post("http://localhost:4000/predict", {
        features,
      });
      navigate("/result", { state: { prediction: response.data.prediction } });
    } catch (err) {
      console.error(err.response.data);
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Foto Penyakit Padi</h2>
      <input type="file" onChange={handleFileUpload} />
      <button onClick={handlePrediction} disabled={!features}>
        Predict
      </button>
    </div>
  );
}

export default Upload;
