import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Paper } from '@mui/material';
import { useNavigate } from 'react-router-dom';

function Auth() {
  const [isLogin, setIsLogin] = useState(true); // State untuk menentukan form Login atau Register
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const navigate = useNavigate();

  // Fungsi untuk menangani submit login
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // Lakukan proses login (misalnya, kirim ke backend untuk autentikasi)
    alert('Login berhasil!');
    navigate('/diagnosis'); // Setelah login, arahkan ke halaman diagnosis
  };

  // Fungsi untuk menangani submit register
  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    // Lakukan proses registrasi (misalnya, kirim data ke backend untuk pendaftaran pengguna)
    alert('Registrasi berhasil!');
    navigate('/login'); // Setelah register, arahkan ke halaman login
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#f7f7f7',
      }}
    >
      <Paper sx={{ padding: 3, width: 400, textAlign: 'center' }}>
        <Typography variant="h5" sx={{ mb: 2 }}>
          {isLogin ? 'Login' : 'Register'}
        </Typography>

        {/* Form Login */}
        {isLogin ? (
          <form onSubmit={handleLoginSubmit}>
            <TextField
              label="Email"
              type="email"
              fullWidth
              required
              margin="normal"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              required
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>
              Login
            </Button>
          </form>
        ) : (
          /* Form Register */
          <form onSubmit={handleRegisterSubmit}>
            <TextField
              label="Full Name"
              type="text"
              fullWidth
              required
              margin="normal"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <TextField
              label="Email"
              type="email"
              fullWidth
              required
              margin="normal"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
              label="Password"
              type="password"
              fullWidth
              required
              margin="normal"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>
              Register
            </Button>
          </form>
        )}

        {/* Toggle between Login and Register */}
        <Box sx={{ mt: 2 }}>
          <Typography variant="body2">
            {isLogin ? "Don't have an account?" : "Already have an account?"}{' '}
            <Button onClick={() => setIsLogin(!isLogin)} sx={{ textTransform: 'none' }}>
              {isLogin ? 'Register here' : 'Login here'}
            </Button>
          </Typography>
        </Box>
      </Paper>
    </Box>
  );
}

export default Auth;
