import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';  // Impor Bootstrap
import { Box, Button, Typography } from '@mui/material'; // Impor Material UI

function Home() {
  return (
    <div
      style={{
        backgroundImage: 'url(/home1)',  // Ganti dengan path gambar yang benar
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',  // Menjamin tinggi halaman penuh layar
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white',  // Warna teks
        position: 'relative',  // Untuk menempatkan elemen di atas background
      }}
    >
      {/* Menggunakan Container, Row, dan Col dari Bootstrap */}
      <Container>
        <Row className="justify-content-center">
          <Col xs={12} md={8} lg={6} className="text-center">
            <h1>Identifikasi Penyakit Padi</h1>
            <p>Dengan menggunakan teknologi AI untuk mendiagnosis penyakit pada tanaman padi</p>
            <Button variant="contained" color="primary" onClick={() => console.log('Mulai Diagnosis')}>
              Mulai Diagnosis
            </Button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Home;
