import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom'; // Import useLocation
import '../App.css';

function NavBar() {
  const [isNavOpen, setIsNavOpen] = useState(false); // State untuk mengontrol apakah navbar terbuka atau tidak
  const location = useLocation(); // Hook untuk mendapatkan lokasi saat ini

  // Efek untuk menutup navbar saat berpindah halaman
  useEffect(() => {
    setIsNavOpen(false); // Tutup navbar jika location berubah
  }, [location]);

  return (
    <Navbar className="navbar navbar-expand-lg navbar-light bg-transparent fixed-top" >
      <Container>
        <Navbar.Brand as={Link} to="/">
          <img 
              src="/images/logo1.png"
              alt="logo"
              style={{ height: '40px', width: 'auto' }}
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" onClick={() => setIsNavOpen(!isNavOpen)} />
        <Navbar.Collapse id="navbar-nav" in={isNavOpen}>
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/tentangKami">Tentang Kami</Nav.Link>
            <Nav.Link as={Link} to="/diagnosis">Diagnosis</Nav.Link>
            <Nav.Link as={Link} to="/history">Riwayat</Nav.Link>
            <Nav.Link as={Link} to="/auth">Login</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
