import React, { useState } from 'react';
import Webcam from 'react-webcam';
import { Box, Button, Typography, Card, CardContent, Input } from '@mui/material';

function Diagnosis() {
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Untuk membuka/menutup kamera
  const [image, setImage] = useState(null); // Menyimpan gambar yang dipilih/tangkap
  const [file, setFile] = useState(null); // Menyimpan file gambar yang diunggah
  const webcamRef = React.useRef(null);

  // Fungsi untuk menangkap foto dari kamera
  const capturePhoto = () => {
    const photo = webcamRef.current.getScreenshot();
    setImage(photo);
    setIsCameraOpen(false); // Menutup kamera setelah foto diambil
  };

  // Fungsi untuk menangani unggahan gambar dari file
  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Mengambil file gambar yang dipilih
    const reader = new FileReader();

    reader.onloadend = () => {
      setFile(reader.result); // Menyimpan gambar ke state
      setImage(reader.result); // Menampilkan gambar yang diunggah
    };
    if (file) {
      reader.readAsDataURL(file); // Membaca file gambar sebagai URL data
    }
  };

  // Fungsi untuk mengirim gambar ke backend untuk diagnosis
  const handleSubmit = () => {
    // Kirim gambar ke backend untuk diagnosis (menggunakan fetch atau axios)
    fetch('/api/diagnose', {
      method: 'POST',
      body: JSON.stringify({ image: image }), // Mengirim gambar sebagai data JSON
      headers: { 'Content-Type': 'application/json' },
    })
      .then((response) => response.json())
      .then((data) => {
        // Menampilkan hasil diagnosis
        alert(`Diagnosis: ${data.diagnosis}\nPenanganan: ${data.treatment}`);
      });
  };

  return (
    <Box
      sx={{
        minHeight: '100vh', 
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        padding: 2,
        overflow: 'auto', // Menghindari potongan pada card
      }}
    >
      <Card
        sx={{
          maxWidth: 400,
          width: '100%',
          minHeight: 500, // Tinggi minimum untuk card
          padding: 2,
          borderRadius: '8px',
        }}
      >
        <CardContent>
          {image ? (
            <img
              src={image}
              alt="Captured or Uploaded"
              style={{
                width: '100%',
                height: 'auto',
                borderRadius: '8px',
                marginBottom: '16px',
              }}
            />
          ) : isCameraOpen ? (
            <Webcam
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              style={{
                width: '100%',
                height: 'auto',
                borderRadius: '8px',
                marginBottom: '16px',
              }}
            />
          ) : (
            <Typography variant="body2" sx={{ mb: 2 }}>
              Unggah atau tangkap foto untuk diagnosis
            </Typography>
          )}

          <Box sx={{ display: 'flex', gap: 2, mt: 2 }}>
            {!isCameraOpen && !image && (
              <>
                <Button variant="contained" onClick={() => setIsCameraOpen(true)}>
                  Buka Kamera
                </Button>
                <Button
                  variant="outlined"
                  component="label"
                  htmlFor="file-input"
                >
                  Unggah Gambar
                  <Input
                    type="file"
                    id="file-input"
                    accept="image/*"
                    hidden
                    onChange={handleFileChange}
                  />
                </Button>
              </>
            )}

            {isCameraOpen && (
              <Button variant="contained" onClick={capturePhoto}>
                Tangkap Foto
              </Button>
            )}

            {image && (
              <Button variant="contained" onClick={handleSubmit}>
                Kirim untuk Diagnosis
              </Button>
            )}
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
}

export default Diagnosis;
