import React from "react";
import { useNavigate } from "react-router-dom";
import Footer from './Footer';

function Home() {
  const navigate = useNavigate(); 
  const handleMulaiIdentifikasi = () => {
    navigate('/diagnosis');
  };

  const handlePelajariLebihLanjut = () => {
    navigate('#tentangKami');
  };

  return (
    <div className="bg-utama">
      {/* Header Utama */}
      <header 
        className="text-white text-center py-5" 
        style={{
          backgroundImage: "url('/images/home-1.jpg')", 
          backgroundSize: 'cover', 
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat'
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">PadiScan</h1>
          <p className="lead fw-bold">Solusi Cerdas Identifikasi Penyakit Padi dengan AI</p>
        </div>
      </header>

      {/* Section 1: Gambaran Singkat */}
      <section className="container py-5">
        <h2 className="text-center mb-4">Kenapa Memilih PadiScan?</h2>
        <div className="row">
          <div className="col-md-4 text-center">
            <h5>Deteksi AI</h5>
            <p>Identifikasi penyakit padi dengan akurasi tinggi menggunakan teknologi AI canggih.</p>
          </div>
          <div className="col-md-4 text-center">
            <h5>Kecepatan Hasil</h5>
            <p>Dapatkan hasil analisis dalam hitungan detik untuk tindakan cepat dan tepat.</p>
          </div>
          <div className="col-md-4 text-center">
            <h5>Kemudahan Penggunaan</h5>
            <p>Antarmuka sederhana yang memudahkan siapa saja untuk mengunggah dan menganalisis data.</p>
          </div>
        </div>
      </section>

      {/* Section 2: Call-to-Action */}
      <section className=" py-5 text-center" id="cta">
        <h2 className="mb-4">Mulai Sekarang</h2>
        <p className="mb-4">Jelajahi fitur kami dan tingkatkan produktivitas pertanian Anda dengan Padiscan.</p>
        <button 
          className="btn btn-success mx-2" 
          onClick={handleMulaiIdentifikasi}  // Navigasi saat tombol diklik
        >
          Mulai Identifikasi Sekarang
        </button>
        <button 
          className="btn btn-outline-success mx-2" 
          onClick={handlePelajariLebihLanjut}  // Navigasi saat tombol diklik
        >
          Pelajari Lebih Lanjut
        </button>
      </section>

      {/* Highlight Teknologi */}
      <section className="container py-5">
        <h2 className="text-center mb-4">Teknologi di Balik Padiscan</h2>
        <div className="row">
          <div className="col-md-6">
            <h5>Kecerdasan Buatan</h5>
            <p>
              Padiscan menggunakan teknologi AI untuk menganalisis data dan mendeteksi penyakit padi dengan
              akurasi tinggi. Sistem ini terus belajar untuk memberikan hasil terbaik kepada pengguna.
            </p>
          </div>
          <div className="col-md-6">
            <h5>Inovasi dan Keandalan</h5>
            <p>
              Kami mengintegrasikan teknologi terkini dalam setiap proses sehingga memberikan solusi yang
              andal dan inovatif untuk meningkatkan hasil pertanian.
            </p>
          </div>
        </div>
      </section>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Home;
