import React from 'react';
import { useLocation } from 'react-router-dom';
import { Box, Typography, Card, CardContent } from '@mui/material';

function HasilDiagnosis() {
  const location = useLocation();
  const { image } = location.state || {}; // Mendapatkan gambar dari state yang dikirimkan

  return (
    <div className="bg-utama">
      {/* Header */}
      <header
        className="text-white text-center py-5"
        style={{
          backgroundImage: "url('/images/home-1.jpg')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">Hasil Diagnosis</h1>
          <p className="lead fw-bold">Berikut adalah hasil diagnosis tanaman padi Anda.</p>
        </div>
      </header>

      {/* Card untuk Hasil Diagnosis */}
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: 2,
        }}
      >
        <Card
          sx={{
            maxWidth: 500,
            width: '100%',
            minHeight: 500,
            padding: 2,
            borderRadius: '8px',
          }}
        >
          <CardContent>
            {image ? (
              <img
                src={image}
                alt="Diagnosis Result"
                style={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: '8px',
                  marginBottom: '16px',
                }}
              />
            ) : (
              <Typography variant="body2">
                Gambar tidak ditemukan.
              </Typography>
            )}
            {/* Hasil diagnosis dan penanganan bisa ditampilkan di sini */}
            <Typography variant="h6" sx={{ marginTop: 2 }}>
              Diagnosis: Tanaman padi Anda sehat
            </Typography>
            <Typography variant="body1">
              Penanganan: Tidak diperlukan tindakan lebih lanjut.
            </Typography>
          </CardContent>
        </Card>
      </Box>
    </div>
  );
}

export default HasilDiagnosis;
