import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, Typography } from '@mui/material';
import { Container, Row, Col, Carousel } from 'react-bootstrap';

function Home() {
  const navigate = useNavigate();

  return (
    <Container>
      <Box
      sx={{
        height: '100vh',  // Menyesuaikan tinggi layar
        display: 'flex',
        justifyContent: 'center',  // Mengatur agar konten berada di tengah
        alignItems: 'center',  // Mengatur konten vertikal di tengah
        textAlign: 'center',
        color: 'white',
      }}
    >
      <div style={{ maxWidth: '100%', height: 'auto' }}>
        <img
          src="/images/slider1.png"  // Ganti dengan path gambar Anda
          alt="Sistem Identifikasi Penyakit Padi"
          style={{
            width: '100%',  // Membuat gambar responsif (mengisi lebar kontainer)
            height: 'auto',  // Menjaga rasio aspek gambar
          }}
        />
      </div>
    </Box>

      {/* Carousel */}
      <div id="carouselExampleSlidesOnly" className="carousel slide" data-bs-ride="carousel"/>
        <div className="carousel-inner"/>
          {/* Slide 1 */}
          <div className="carousel-item active">
            <img
              src="images/slider1.png" // Ganti dengan path gambar yang sesuai
              className="d-block w-100"
              alt="Slide 1"
            />
          </div>
          {/* Slide 2 */}
          <div className="carousel-item"/>
            <img
              src="images/slider2.png" // Ganti dengan path gambar yang sesuai
              className="d-block w-100"
              alt="Slide 2"
            />

      <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        backgroundImage: 'url(/path-to-paddy-image.jpg)', // Ganti dengan path yang sesuai
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <Typography variant="h3" sx={{ color: '#fff', mb: 4 }}>
        Sistem Identifikasi Penyakit Padi
      </Typography>
      <Box sx={{ display: 'flex', gap: 2 }}>
        <Button variant="contained" onClick={() => navigate('/register')}>Register</Button>
        <Button variant="contained" onClick={() => navigate('/login')}>Login</Button>
        <Button variant="outlined" onClick={() => navigate('/diagnosis')}>
          Lanjutkan Tanpa Login
        </Button>
      </Box>
    </Box>


    </Container>
  );
}

export default Home;
