import React, { useState } from 'react';
import { Button, Form, Alert, Container, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    if (!email || !password) {
      setError('Email dan password harus diisi');
      return;
    }
    // Simulasi login berhasil
    localStorage.setItem('isLoggedIn', 'true');
    navigate('/dashboard'); // Ganti dengan halaman tujuan setelah login
  };

  return (
    <Container fluid className="d-flex align-items-center justify-content-center" style={{ minHeight: '100vh', backgroundColor: '#f1f1f1' }}>
      <Row className="w-100">
        {/* Form Login */}
        <Col xs={12} md={6} className="d-flex justify-content-center align-items-center p-0">
          <div className="w-100 bg-white p-4 shadow rounded">
            <h3 className="mb-4 text-center">Login</h3>
            {error && <Alert variant="danger">{error}</Alert>}

            <Form onSubmit={handleLoginSubmit}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </Form.Group>

              <Button variant="primary" type="submit" className="w-100">
                Login
              </Button>
            </Form>

            <div className="mt-3 text-center">
              <small>
                Don't have an account?{' '}
                <Button variant="link" onClick={() => navigate('/register')}>
                  Register here
                </Button>
              </small>
            </div>
          </div>
        </Col>

        
      </Row>
    </Container>
  );
}

export default Login;
