import React, { useState, useEffect } from 'react';
import { Navbar, Nav } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import '../App.css'; 

function NavBar() {
  const [isNavOpen, setIsNavOpen] = useState(false); 
  const location = useLocation(); 
  const [prevScrollPos, setPrevScrollPos] = useState(0); 
  const [visible, setVisible] = useState(true); 
  
  // Efek untuk menutup navbar saat berpindah halaman
  useEffect(() => {
    setIsNavOpen(false);
  }, [location]);

  // Fungsi untuk menangani scroll
  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;
    const isVisible = prevScrollPos > currentScrollPos || currentScrollPos < 10;
    setVisible(isVisible);
    setPrevScrollPos(currentScrollPos);
  };

  // Hook untuk mendeteksi scroll event
  useEffect(() => {
    window.addEventListener('scroll', handleScroll); 
    return () => {
      window.removeEventListener('scroll', handleScroll); 
    };
  }, [prevScrollPos]);

  return (
    <Navbar
      className={`navbar-fixed-top navbar-custom ${visible ? 'navbar-visible' : 'navbar-hidden'}`}
      expand="lg"
      expanded={isNavOpen}
      style={{ width: '100%', zIndex: '1000' }}
    >
      <Navbar.Brand as={Link} to="/">
        <img 
          src="/images/logo1.png"
          alt="logo"
          style={{ height: '40px', width: 'auto' }}
        />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbar-nav" onClick={() => setIsNavOpen(!isNavOpen)} />
      <Navbar.Collapse id="navbar-nav">
        <Nav className="ms-auto">
          <Nav.Link as={Link} to="/">Home</Nav.Link>
          <Nav.Link as={Link} to="/tentangKami">Tentang Kami</Nav.Link>
          <Nav.Link as={Link} to="/diagnosis">Diagnosis</Nav.Link>
          <Nav.Link as={Link} to="/history">Riwayat</Nav.Link>
          <Nav.Link as={Link} to="/auth">Login</Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
}

export default NavBar;
