import React, { useState } from 'react';
import Webcam from 'react-webcam';
import { Box, Button, Typography, Card, CardContent } from '@mui/material';

function App() {
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const [image, setImage] = useState(null);
  const [file, setFile] = useState(null);

  const webcamRef = React.useRef(null);

  const capturePhoto = () => {
    const photo = webcamRef.current.getScreenshot();
    setImage(photo);
    setIsCameraOpen(false);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFile(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <Box 
      sx={{ 
        minHeight: '100vh', 
        display: 'flex', 
        flexDirection: 'column', 
        justifyContent: 'center', 
        alignItems: 'center', 
        padding: 2, 
        backgroundColor: '#f7f7f7' 
      }}
    >
      <Typography variant="h4" sx={{ marginBottom: 3, textAlign: 'center' }}>
        Paddy Disease Identifier
      </Typography>

      <Card sx={{ maxWidth: 400, padding: 2 }}>
        <CardContent>
          <Box sx={{ marginBottom: 2 }}>
            {image ? (
              <img src={image} alt="Captured" style={{ width: '100%', borderRadius: '8px' }} />
            ) : file ? (
              <img src={file} alt="Uploaded" style={{ width: '100%', borderRadius: '8px' }} />
            ) : (
              <Typography variant="body2">No image uploaded or captured</Typography>
            )}
          </Box>

          {isCameraOpen && (
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              style={{ width: '100%', borderRadius: '8px' }}
            />
          )}

          <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', justifyContent: 'center' }}>
            {!isCameraOpen && (
              <Button
                variant="contained"
                color="primary"
                onClick={() => setIsCameraOpen(true)}
              >
                Open Camera
              </Button>
            )}

            {isCameraOpen && (
              <Button
                variant="contained"
                color="success"
                onClick={capturePhoto}
              >
                Capture Photo
              </Button>
            )}

            <Button
              variant="outlined"
              component="label"
            >
              Upload File
              <input
                type="file"
                hidden
                accept="image/*"
                onChange={handleFileUpload}
              />
            </Button>

            {(image || file) && (
              <Button
                variant="contained"
                color="secondary"
                onClick={() => { setImage(null); setFile(null); }}
              >
                Reset
              </Button>
            )}
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
}

export default App;
