import React, { useState } from "react";
import Webcam from "react-webcam";
import { useNavigate } from "react-router-dom"; // Untuk navigasi
import NavBarDashboard from './NavBarDashboard'; // Navbar yang digunakan sebelumnya

function Diagnosis() {
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Untuk membuka/menutup kamera
  const [image, setImage] = useState(null); // Menyimpan gambar yang dipilih/tangkap
  const [file, setFile] = useState(null); // Menyimpan file gambar yang diunggah
  const webcamRef = React.useRef(null);
  const navigate = useNavigate(); // Hook untuk navigasi

  // Fungsi untuk menangkap foto dari kamera
  const capturePhoto = () => {
    const photo = webcamRef.current.getScreenshot();
    setImage(photo);
    setIsCameraOpen(false); // Menutup kamera setelah foto diambil
  };

  // Fungsi untuk menangani unggahan gambar dari file
  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Mengambil file gambar yang dipilih
    const reader = new FileReader();

    reader.onloadend = () => {
      setFile(reader.result); // Menyimpan gambar ke state
      setImage(reader.result); // Menampilkan gambar yang diunggah
    };
    if (file) {
      reader.readAsDataURL(file); // Membaca file gambar sebagai URL data
    }
  };

  // Fungsi untuk menavigasi ke halaman hasil diagnosis setelah foto diunggah
  const handleSubmit = () => {
    if (!image) {
      alert("Mohon unggah atau ambil foto terlebih dahulu!");
      return;
    }
    
    // Menavigasi ke halaman hasil diagnosis setelah foto diunggah
    navigate("/hasilDiagnosis", { state: { image } });
  };

  return (
    <div className="bg-utama">
      {/* Navbar */}
      <NavBarDashboard />

      {/* Header */}
      <header
        className="text-white text-center py-5"
        style={{
          backgroundImage: "url('/images/home-1.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          backgroundRepeat: "no-repeat",
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">Halaman Diagnosis</h1>
          <p className="lead fw-bold">
            Unggah atau ambil foto untuk mendapatkan diagnosis tanaman padi Anda.
          </p>
        </div>
      </header>

      {/* Section: Gambaran Singkat Diagnosis */}
      <section className="container py-5">
        <h3 className="text-center text-success mb-4">Kenapa Menggunakan PadiScan?</h3>
        <p className="text-center mb-4">
          PadiScan menggunakan teknologi canggih untuk mengidentifikasi penyakit padi dengan cepat dan akurat.
        </p>

        {/* Cards - Deteksi AI, Kecepatan, Kemudahan */}
        <div className="row text-center">
          <div className="col-md-4">
            <div className="card shadow border-0 p-4">
              <div className="d-flex justify-content-center mb-3">
                <img src="/images/home-icon-1.png" alt="AI Icon" style={{ width: '50px', height: '50px' }} />
              </div>
              <h5 className="fw-bold">Deteksi AI</h5>
              <p className="mb-0">
                Identifikasi penyakit padi dengan akurasi tinggi menggunakan teknologi AI canggih.
              </p>
            </div>
          </div>

          <div className="col-md-4">
            <div className="card shadow border-0 p-4">
              <div className="d-flex justify-content-center mb-3">
                <img src="/images/home-icon-2.png" alt="Speed Icon" style={{ width: '50px', height: '50px' }} />
              </div>
              <h5 className="fw-bold">Kecepatan Hasil</h5>
              <p className="mb-0">
                Dapatkan hasil analisis dalam hitungan detik untuk tindakan cepat dan tepat.
              </p>
            </div>
          </div>

          <div className="col-md-4">
            <div className="card shadow border-0 p-4">
              <div className="d-flex justify-content-center mb-3">
                <img src="/images/home-icon-3.png" alt="Ease Icon" style={{ width: '50px', height: '50px' }} />
              </div>
              <h5 className="fw-bold">Kemudahan Penggunaan</h5>
              <p className="mb-0">
                Antarmuka sederhana yang memudahkan siapa saja untuk mengunggah dan menganalisis data.
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Formulir Diagnosis */}
      <section className="container py-5">
        <h4 className="text-center mb-4">Cara Menggunakan:</h4>
        <div className="row text-center mb-4">
          <div className="col-md-4">
            <h5 className="fw-bold">1</h5>
            <p>Buka Kamera</p>
          </div>
          <div className="col-md-4">
            <h5 className="fw-bold">2</h5>
            <p>Unggah Gambar</p>
          </div>
          <div className="col-md-4">
            <h5 className="fw-bold">3</h5>
            <p>Kirim Foto untuk Diagnosis</p>
          </div>
        </div>

        <p className="text-center mb-4">
          Pastikan gambar yang Anda unggah jelas dan terang, dengan fokus yang tepat pada tanaman.
        </p>

        {/* Webcam Section */}
        <div className="mb-4">
          {image ? (
            <img
              src={image}
              alt="Captured or Uploaded"
              className="img-fluid rounded mb-3"
            />
          ) : isCameraOpen ? (
            <>
              <Webcam
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                width="100%"
                videoConstraints={{
                  facingMode: "environment",
                }}
                className="mb-3"
              />
              <button
                className="btn btn-success w-100"
                onClick={capturePhoto}
              >
                Ambil Foto
              </button>
            </>
          ) : (
            <p className="text-center mb-3">Unggah atau tangkap foto untuk diagnosis</p>
          )}
        </div>

        {/* File Input & Submit Button */}
        <div className="d-flex justify-content-between">
          {!isCameraOpen && !image && (
            <button
              className="btn btn-primary w-48"
              onClick={() => setIsCameraOpen(true)}
            >
              Buka Kamera
            </button>
          )}

          {!isCameraOpen && !image && (
            <input
              type="file"
              onChange={handleFileChange}
              accept="image/*"
              className="form-control w-48"
            />
          )}

          <button
            className="btn btn-success w-48"
            onClick={handleSubmit}
          >
            Kirim Foto
          </button>
        </div>
      </section>
    </div>
  );
}

export default Diagnosis;
