import React, { useState } from 'react';
import Webcam from 'react-webcam';
import { Button, Typography, Input, Box } from '@mui/material';
import NavBarDashboard from './NavBarDashboard'; // Navbar yang digunakan sebelumnya

function Diagnosis() {
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Untuk membuka/menutup kamera
  const [image, setImage] = useState(null); // Menyimpan gambar yang dipilih/tangkap
  const [file, setFile] = useState(null); // Menyimpan file gambar yang diunggah
  const webcamRef = React.useRef(null);

  // Fungsi untuk menangkap foto dari kamera
  const capturePhoto = () => {
    const photo = webcamRef.current.getScreenshot();
    setImage(photo);
    setIsCameraOpen(false); // Menutup kamera setelah foto diambil
  };

  // Fungsi untuk menangani unggahan gambar dari file
  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Mengambil file gambar yang dipilih
    const reader = new FileReader();

    reader.onloadend = () => {
      setFile(reader.result); // Menyimpan gambar ke state
      setImage(reader.result); // Menampilkan gambar yang diunggah
    };
    if (file) {
      reader.readAsDataURL(file); // Membaca file gambar sebagai URL data
    }
  };

  return (
    <div className="bg-utama">
      {/* Navbar */}
      <NavBarDashboard />

      {/* Header */}
      <header
        className="text-white text-center py-5"
        style={{
          backgroundImage: "url('/images/home-1.jpg')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">Halaman Diagnosis</h1>
          <p className="lead fw-bold">Unggah atau ambil foto untuk mendapatkan diagnosis tanaman padi Anda.</p>
        </div>
      </header>

      {/* Konten Diagnosis */}
      <div className="container text-center mt-5">
        <Typography variant="h5" gutterBottom>
          {image ? 'Foto yang diunggah / Diambil' : 'Unggah atau ambil foto terlebih dahulu'}
        </Typography>

        {/* Menampilkan gambar yang diambil atau diunggah */}
        {image ? (
          <img
            src={image}
            alt="Captured or Uploaded"
            style={{
              width: '100%',
              height: 'auto',
              maxHeight: '300px',
              borderRadius: '8px',
              marginBottom: '16px',
            }}
          />
        ) : (
          <Typography variant="body2" sx={{ mb: 2 }}>
            Belum ada foto yang diunggah atau diambil.
          </Typography>
        )}

        {/* Pilihan untuk Buka Kamera atau Unggah Foto */}
        <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 2 }}>
          {/* Tombol untuk buka kamera */}
          {!isCameraOpen && !image && (
            <Button variant="contained" color="primary" onClick={() => setIsCameraOpen(true)}>
              Buka Kamera
            </Button>
          )}

          {/* Tombol untuk memilih file gambar */}
          {!image && (
            <Button
              variant="outlined"
              component="label"
              htmlFor="file-input"
            >
              Unggah Foto
              <Input
                type="file"
                id="file-input"
                accept="image/*"
                hidden
                onChange={handleFileChange}
              />
            </Button>
          )}

          {/* Tombol untuk mengambil gambar jika kamera dibuka */}
          {isCameraOpen && (
            <Button variant="contained" color="secondary" onClick={capturePhoto}>
              Ambil Foto
            </Button>
          )}
        </Box>
      </div>
    </div>
  );
}

export default Diagnosis;
