import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom'; // Import useLocation
import '../App.css';

function NavBar() {
  const [isNavOpen, setIsNavOpen] = useState(false); // State untuk mengontrol apakah navbar terbuka atau tidak
  const location = useLocation(); // Hook untuk mendapatkan lokasi saat ini
  const [prevScrollPos, setPrevScrollPos] = useState(0);  // Menyimpan posisi scroll sebelumnya
  const [visible, setVisible] = useState(true);  // Menyimpan status visibilitas navbar

  // Efek untuk menutup navbar saat berpindah halaman
  useEffect(() => {
    setIsNavOpen(false);  // Tutup navbar jika location berubah
  }, [location]);

  // Fungsi untuk menangani scroll
  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;  // Mendapatkan posisi scroll saat ini
    if (currentScrollPos > prevScrollPos) {
      setVisible(false);  // Sembunyikan navbar saat scroll ke bawah
    } else {
      setVisible(true);  // Tampilkan navbar saat scroll ke atas
    }
    setPrevScrollPos(currentScrollPos);  // Update posisi scroll sebelumnya
  };

  // Hook untuk mendeteksi scroll event
  useEffect(() => {
    window.addEventListener('scroll', handleScroll);  // Menambahkan event listener untuk scroll
    return () => {
      window.removeEventListener('scroll', handleScroll);  // Membersihkan event listener saat komponen tidak digunakan
    };
  }, [prevScrollPos]);

  useEffect(() => {
    setIsNavOpen(false); // Tutup navbar jika location berubah
  }, [location]);

  return (
    <Navbar
      className={`navbar-fixed-top ${visible ? 'navbar-visible' : 'navbar-hidden'}`}  // Mengatur kelas CSS untuk menampilkan atau menyembunyikan navbar
      expand="lg"
      bg="dark"
      variant="dark"
    >
      <Container>
        <Navbar.Brand as={Link} to="/">Sistem Identifikasi Penyakit Padi</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" />
        <Navbar.Collapse id="navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/about">Tentang Kami</Nav.Link>
            <Nav.Link as={Link} to="/diagnosis">Diagnosis</Nav.Link>
            <Nav.Link as={Link} to="/contact">Kontak</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
