import React, { useState } from 'react';
import Webcam from 'react-webcam';
import { Button, Typography, Input, Container, Box, Grid } from '@mui/material';
import NavBarDashboard from './NavBarDashboard'; // Navbar yang digunakan sebelumnya
import { useNavigate } from 'react-router-dom'; // Mengimpor useNavigate untuk navigasi

function Diagnosis() {
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Untuk membuka/menutup kamera
  const [image, setImage] = useState(null); // Menyimpan gambar yang dipilih/tangkap
  const [file, setFile] = useState(null); // Menyimpan file gambar yang diunggah
  const webcamRef = React.useRef(null);
  const navigate = useNavigate(); // Hook untuk navigasi

  // Fungsi untuk menangkap foto dari kamera
  const capturePhoto = () => {
    const photo = webcamRef.current.getScreenshot();
    setImage(photo);
    setIsCameraOpen(false); // Menutup kamera setelah foto diambil
  };

  // Fungsi untuk menangani unggahan gambar dari file
  const handleFileChange = (event) => {
    const file = event.target.files[0]; // Mengambil file gambar yang dipilih
    const reader = new FileReader();

    reader.onloadend = () => {
      setFile(reader.result); // Menyimpan gambar ke state
      setImage(reader.result); // Menampilkan gambar yang diunggah
    };
    if (file) {
      reader.readAsDataURL(file); // Membaca file gambar sebagai URL data
    }
  };

  // Fungsi untuk menavigasi ke halaman hasil diagnosis setelah foto diunggah
  const handleSubmit = () => {
    if (!image) {
      alert('Mohon unggah atau ambil foto terlebih dahulu!');
      return;
    }
    
    // Menavigasi ke halaman hasil diagnosis setelah foto diunggah
    navigate('/hasilDiagnosis', { state: { image } });
  };

  return (
    <div className="bg-utama">
      {/* Navbar */}
      <NavBarDashboard />

      {/* Header */}
      <header
        className="text-white text-center py-5"
        style={{
          backgroundImage: "url('/images/home-1.jpg')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
        }}
      >
        <div className="container">
          <h1 className="display-4 fw-bold">Halaman Diagnosis</h1>
          <p className="lead fw-bold">Unggah atau ambil foto untuk mendapatkan diagnosis tanaman padi Anda.</p>
        </div>
      </header>

      {/* Main Content */}
      <Container maxWidth="sm" sx={{ paddingTop: 4 }}>
        {/* Cara Menggunakan Section */}
        <Box sx={{ marginBottom: 3 }}>
          <Typography variant="h6" sx={{ fontWeight: 'bold', marginBottom: 2 }}>
            Cara Menggunakan:
          </Typography>

          {/* Grid Layout for Steps with Icons */}
          <Grid container spacing={2}>
            <Grid item xs={4} sx={{ textAlign: 'center' }}>
              <Box sx={{ marginBottom: 1 }}>
                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>1</Typography>
              </Box>
              <Typography variant="body2">Buka Kamera</Typography>
            </Grid>
            <Grid item xs={4} sx={{ textAlign: 'center' }}>
              <Box sx={{ marginBottom: 1 }}>
                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>2</Typography>
              </Box>
              <Typography variant="body2">Unggah Gambar</Typography>
            </Grid>
            <Grid item xs={4} sx={{ textAlign: 'center' }}>
              <Box sx={{ marginBottom: 1 }}>
                <Typography variant="h4" sx={{ fontWeight: 'bold' }}>3</Typography>
              </Box>
              <Typography variant="body2">Kirim Foto untuk Diagnosis</Typography>
            </Grid>
          </Grid>

          {/* Keterangan tentang kualitas foto */}
          <Typography variant="body2" sx={{ marginTop: 2, color: '#555' }}>
            Pastikan gambar yang Anda unggah jelas dan terang, dengan fokus yang tepat pada tanaman.
          </Typography>
        </Box>

        {/* Box untuk gambar dan tombol */}
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: '60vh', // Membuat box hampir memenuhi layar
            padding: 2,
            backgroundColor: '#ffffff',
            borderRadius: '8px',
            boxShadow: '0px 4px 12px rgba(0, 0, 0, 0.1)',
          }}
        >
          {/* Gambar atau Webcam */}
          <div style={{ textAlign: 'center', marginBottom: '24px' }}>
            {image ? (
              <img
                src={image}
                alt="Captured or Uploaded"
                style={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: '8px',
                  marginBottom: '16px',
                  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                }}
              />
            ) : isCameraOpen ? (
              <>
                <Webcam
                  ref={webcamRef}
                  screenshotFormat="image/jpeg"
                  style={{
                    width: '100%',
                    height: 'auto',
                    borderRadius: '8px',
                    marginBottom: '16px',
                    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                  }}
                />
                <Button
                  variant="contained"
                  onClick={capturePhoto}
                  sx={{
                    width: '100%',
                    marginBottom: 2,
                    backgroundColor: '#4caf50',
                    '&:hover': {
                      backgroundColor: '#45a049',
                    },
                  }}
                >
                  Ambil Foto
                </Button>
              </>
            ) : (
              <Typography variant="body2" sx={{ mb: 2, textAlign: 'center' }}>
                Unggah atau tangkap foto untuk diagnosis
              </Typography>
            )}
          </div>

          {/* Box untuk tombol */}
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'space-between',
              width: '100%',
              marginBottom: 2,
            }}
          >
            {/* Tombol untuk membuka kamera di kanan */}
            {!isCameraOpen && !image && (
              <Button
                variant="contained"
                onClick={() => setIsCameraOpen(true)}
                sx={{
                  width: '48%',
                  borderRadius: '8px',
                  backgroundColor: '#4caf50',
                  '&:hover': {
                    backgroundColor: '#45a049',
                  },
                }}
              >
                Buka Kamera
              </Button>
            )}

            {/* Tombol untuk unggah gambar di kiri */}
            {!isCameraOpen && !image && (
              <Input
                type="file"
                onChange={handleFileChange}
                inputProps={{ accept: 'image/*' }}
                sx={{
                  display: 'block',
                  width: '48%',
                  marginBottom: 2,
                  backgroundColor: 'white',
                  borderRadius: '8px',
                  padding: '8px',
                }}
              />
            )}
          </Box>

          {/* Tombol untuk mengirim gambar */}
          <Button
            variant="contained"
            color="primary"
            onClick={handleSubmit}
            sx={{
              width: '100%',
              borderRadius: '8px',
              padding: '12px',
              backgroundColor: '#4caf50',
              '&:hover': {
                backgroundColor: '#45a049',
              },
              marginTop: 2,
            }}
          >
            Kirim Foto untuk Diagnosis
          </Button>
        </Box>
      </Container>
    </div>
  );
}

export default Diagnosis;
