import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom'; 
import '../App.css';

function NavBar() {
  const [isNavOpen, setIsNavOpen] = useState(false); 
  const location = useLocation(); 
  const [prevScrollPos, setPrevScrollPos] = useState(0);
  // Efek untuk menutup navbar saat berpindah halaman
  useEffect(() => {
    setIsNavOpen(false); // Tutup navbar jika location berubah
  }, [location]);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.pageYOffset;
      if (currentScrollPos > prevScrollPos) {
        setIsNavVisible(false); // Navbar tersembunyi
      } else {
        setIsNavVisible(true);  // Navbar muncul
      }
      setPrevScrollPos(currentScrollPos); // Update posisi scroll sebelumnya
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, [prevScrollPos]);

  // Fungsi untuk menangani klik pada tombol navbar
  const handleNavToggle = () => {
    setIsNavOpen(!isNavOpen); // Mengubah state isNavOpen
  };

  useEffect(() => {
    if (isNavOpen) {
      document.body.classList.add('navbar-open');
    } else {
      document.body.classList.remove('navbar-open');
    }
  }, [isNavOpen]);  

  return (
    <Navbar className="navbar  navbar-light bg-transparent fixed-top" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">
          <img 
              src="/images/logo1.png"
              alt="logo"
              style={{ height: '40px', width: 'auto' }}
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="navbar-nav" onClick={() => setIsNavOpen(!isNavOpen)} />
        <Navbar.Collapse id="navbar-nav" in={isNavOpen}>
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/tentangKami">Tentang Kami</Nav.Link>
            <Nav.Link as={Link} to="/diagnosis">Diagnosis</Nav.Link>
            <Nav.Link as={Link} to="/history">Riwayat</Nav.Link>
            <Nav.Link as={Link} to="/auth">Login</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
